import React, { useEffect } from "react";
import { useFormik } from "formik";
import {
  Box,
  Button,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Heading,
  Input,
  Select,
  Textarea,
  VStack,
} from "@chakra-ui/react";
import * as Yup from 'yup';
import FullScreenSection from "./FullScreenSection";
import useSubmit from "../hooks/useSubmit";
import { useAlertContext } from "../context/alertContext";

const LandingSection = () => {
  const { isLoading, response, submit } = useSubmit(); 
  const { onOpen } = useAlertContext(); 

  // Formik configuration
  const formik = useFormik({
    initialValues: {
      firstName: '',
      email: '',
      type: '',
      comment: '',
    },
    validationSchema: Yup.object({
      firstName: Yup.string()
        .min(2, 'First name must be at least 2 characters')
        .required('First name is required'),
      email: Yup.string()
        .email('Invalid email address')
        .required('Email is required'),
      type: Yup.string()
        .required('Please select a type of enquiry'),
      comment: Yup.string()
        .min(10, 'Comment must be at least 10 characters')
        .required('Comment is required'),
    }),
    onSubmit: async (values, { resetForm }) => {
      await submit(values);

      if (response?.type === 'success') {
        onOpen('success', `Thank you ${values.firstName}, your message has been received!`);
        resetForm(); // Reset the form if successful
      } else if (response?.type === 'error') {
        onOpen('error', 'Something went wrong. Please try again.');
      }
    },
  });

  // Handle response changes for showing alerts
  useEffect(() => {
    if (response?.type === 'success') {
      onOpen('success', `Thank you ${formik.values.firstName}, your message has been received!`);
      formik.resetForm();
    } else if (response?.type === 'error') {
      onOpen('error', 'Something went wrong. Please try again.');
    }
  }, [response, onOpen, formik]);

  // Reusable field component
  const Field = ({ id, name, label, component, type = 'text', placeholder }) => (
    <FormControl isInvalid={formik.touched[name] && formik.errors[name]}>
      <FormLabel htmlFor={id}>{label}</FormLabel>
      {component === "input" ? (
        <Input
          id={id}
          name={name}
          type={type}
          placeholder={placeholder}
          value={formik.values[name]}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
      ) : component === "textarea" ? (
        <Textarea
          id={id}
          name={name}
          placeholder={placeholder}
          value={formik.values[name]}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        />
      ) : (
        <Select
          id={id}
          name={name}
          value={formik.values[name]}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
        >
          <option value="">Select a type</option>
          <option value="hireMe">Freelance project proposal</option>
          <option value="openSource">Open source consultancy session</option>
          <option value="other">Other</option>
        </Select>
      )}
      <FormErrorMessage>{formik.errors[name]}</FormErrorMessage>
    </FormControl>
  );

  return (
    <FullScreenSection
      isDarkBackground
      backgroundColor="#512DA8"
      py={16}
      spacing={8}
    >
      <VStack w="1024px" p={32} alignItems="flex-start">
        <Heading as="h1" id="contactme-section">
          Contact me
        </Heading>
        <Box p={6} rounded="md" w="100%">
          <form onSubmit={formik.handleSubmit}>
            <VStack spacing={4}>
              {/* Reusable Field Components */}
              <Field
                id="firstName"
                name="firstName"
                label="Name"
                component="input"
                placeholder="Enter your name"
              />
              <Field
                id="email"
                name="email"
                label="Email Address"
                component="input"
                type="email"
                placeholder="Enter your email"
              />
              <Field
                id="type"
                name="type"
                label="Type of enquiry"
                component="select"
              />
              <Field
                id="comment"
                name="comment"
                label="Your message"
                component="textarea"
                placeholder="Enter your message"
              />

              {/* Submit Button */}
              <Button
                type="submit"
                colorScheme="purple"
                width="full"
                isLoading={isLoading}
                isDisabled={formik.isSubmitting || !formik.isValid} // Disable button if there are errors
              >
                Submit
              </Button>
            </VStack>
          </form>
        </Box>
      </VStack>
    </FullScreenSection>
  );
};

export default LandingSection;
